import { useState } from "react";

const catalogBig = [
  {
    id: 0,
    title: "Постельное белье",
    src: "/bedroom/bed_newline.png",
    type: "bedroom",
  },
  {
    id: 1,
    title: "Подушки",
    src: "/bedroom/pillows.png",
    type: "bedroom",
  },

  {
    id: 2,
    title: "Халаты",
    src: "/clothes/bed_newline.png",
    type: "clothes",
  },
  {
    id: 3,
    title: "Тапочки",
    src: "/clothes/pillows.png",
    type: "clothes",
  },

  {
    id: 4,
    title: "Подушки на стул",
    src: "/kitchen/bed_newline.png",
    type: "kitchen",
  },
  {
    id: 5,
    title: "Прихватки",
    src: "/kitchen/pillows.png",
    type: "kitchen",
  },
];

const catalogSmall = [
  {
    id: 0,
    title: "Детская коллекция",
    src: "/bedroom/bed_newline_sm.png",
    type: "bedroom",
  },
  {
    id: 1,
    title: "Покрывала",
    src: "/bedroom/bed_newline_sm.png",
    type: "bedroom",
  },
  {
    id: 2,
    title: "Одеяла",
    src: "/bedroom/blankets.png",
    type: "bedroom",
  },
  {
    id: 3,
    title: "Пледы",
    src: "/bedroom/bed_newline_sm.png",
    type: "bedroom",
  },
  {
    id: 4,
    title: "Наматрисник",
    src: "/bedroom/bed_newline_sm.png",
    type: "bedroom",
  },
  {
    id: 5,
    title: "Матрасы",
    src: "/bedroom/bed_newline_sm.png",
    type: "bedroom",
  },

  {
    id: 6,
    title: "Платки",
    src: "/clothes/bed_newline_sm.png",
    type: "clothes",
  },
  {
    id: 7,
    title: "Покрывала",
    src: "/clothes/bed_newline_sm.png",
    type: "clothes",
  },
  {
    id: 8,
    title: "Пледики для СПА",
    src: "/clothes/blankets.png",
    type: "clothes",
  },
  {
    id: 9,
    title: "Шоперы",
    src: "/clothes/bed_newline_sm.png",
    type: "clothes",
  },

  {
    id: 10,
    title: "Декоративные чехлы",
    src: "/kitchen/bed_newline_sm.png",
    type: "kitchen",
  },
  {
    id: 11,
    title: "Чехлы для табурета",
    src: "/kitchen/bed_newline_sm.png",
    type: "kitchen",
  },
  {
    id: 12,
    title: "Органайзер",
    src: "/kitchen/blankets.png",
    type: "kitchen",
  },
  {
    id: 13,
    title: "Чехол для одежды",
    src: "/kitchen/bed_newline_sm.png",
    type: "kitchen",
  },
  {
    id: 14,
    title: "Полотенца",
    src: "/kitchen/blankets.png",
    type: "kitchen",
  },
];

function Catalog({ setPull, pull, setCatalogChapter, catalogChapter }) {
  const [catalogMenuImgActive, setCatalogMenuImgActive] = useState("");
  const [catalogActive, setCatalogActive] = useState("");
  const [catalogStockActive, setStockCatalogActive] = useState(
    " pull-stock-catalog-active"
  );

  return (
    <div className={"pull__main" + pull}>
      <div className="pull-menu">
        <ul className="pull-menu__list">
          <li
            className="pull-menu__item"
            onClick={() => {
              if (catalogChapter == "bedroom") {
                setCatalogMenuImgActive(
                  " pull-menu__item__catalog-img-disable"
                );
                setCatalogActive("");
                setStockCatalogActive("");
                setTimeout(() => {
                  setStockCatalogActive(" pull-stock-catalog-active");
                  setCatalogChapter("");
                }, 500);
              } else {
                if (catalogChapter == "") {
                  setCatalogActive("");
                  setTimeout(() => {
                    setCatalogActive(" pull-catalog-active");
                  }, 250);
                  setCatalogChapter("bedroom");
                  setTimeout(() => {
                    setCatalogMenuImgActive(
                      " pull-menu__item__catalog-img-active"
                    );
                  }, 1);
                } else {
                  setCatalogMenuImgActive(
                    " pull-menu__item__catalog-img-disable"
                  );
                  setCatalogActive("");
                  setTimeout(() => {
                    setCatalogChapter("bedroom");
                    setTimeout(() => {
                      setCatalogActive(" pull-catalog-active");
                    }, 150);
                    setTimeout(() => {
                      setCatalogMenuImgActive(
                        " pull-menu__item__catalog-img-active"
                      );
                    }, 1);
                  }, 550);
                }
              }
            }}
          >
            Спальня
          </li>
          {catalogChapter == "bedroom" ? (
            <img
              src="./images/header/catalog_bedroom.png"
              className={"pull-menu__item__catalog-img " + catalogMenuImgActive}
            />
          ) : (
            ""
          )}

          <li
            className="pull-menu__item"
            onClick={() => {
              if (catalogChapter == "clothes") {
                setCatalogMenuImgActive(
                  " pull-menu__item__catalog-img-disable"
                );
                setCatalogActive("");
                setTimeout(() => {
                  setCatalogChapter("");
                }, 500);
              } else {
                if (catalogChapter == "") {
                  setCatalogActive("");
                  setCatalogChapter("clothes");
                  setTimeout(() => {
                    setCatalogActive(" pull-catalog-active");
                  }, 300);
                  setTimeout(() => {
                    setCatalogMenuImgActive(
                      " pull-menu__item__catalog-img-active"
                    );
                  }, 1);
                } else {
                  setCatalogMenuImgActive(
                    " pull-menu__item__catalog-img-disable"
                  );
                  setCatalogActive("");
                  setTimeout(() => {
                    setCatalogChapter("clothes");
                    setTimeout(() => {
                      setCatalogActive(" pull-catalog-active");
                    }, 150);
                    setTimeout(() => {
                      setCatalogMenuImgActive(
                        " pull-menu__item__catalog-img-active"
                      );
                    }, 1);
                  }, 550);
                }
              }
            }}
          >
            Одежда и обувь для дома
          </li>
          {catalogChapter == "clothes" ? (
            <img
              src="./images/header/catalog_clothes.png"
              className={"pull-menu__item__catalog-img " + catalogMenuImgActive}
            />
          ) : (
            ""
          )}

          <li
            className="pull-menu__item"
            onClick={() => {
              if (catalogChapter == "kitchen") {
                setCatalogMenuImgActive(
                  " pull-menu__item__catalog-img-disable"
                );
                setCatalogActive("");
                setTimeout(() => {
                  setCatalogChapter("");
                }, 500);
              } else {
                if (catalogChapter == "") {
                  setCatalogActive("");
                  setCatalogChapter("kitchen");
                  setTimeout(() => {
                    setCatalogActive(" pull-catalog-active");
                  }, 300);
                  setTimeout(() => {
                    setCatalogMenuImgActive(
                      " pull-menu__item__catalog-img-active"
                    );
                  }, 1);
                } else {
                  setCatalogMenuImgActive(
                    " pull-menu__item__catalog-img-disable"
                  );
                  setCatalogActive("");
                  setTimeout(() => {
                    setCatalogChapter("kitchen");
                    setTimeout(() => {
                      setCatalogActive(" pull-catalog-active");
                    }, 150);
                    setTimeout(() => {
                      setCatalogMenuImgActive(
                        " pull-menu__item__catalog-img-active"
                      );
                    }, 1);
                  }, 550);
                }
              }
            }}
          >
            Кухня и интерьер
          </li>
          {catalogChapter == "kitchen" ? (
            <img
              src="./images/header/catalog_kitchen.png"
              className={"pull-menu__item__catalog-img " + catalogMenuImgActive}
            />
          ) : (
            ""
          )}
        </ul>
      </div>

      {catalogChapter == "" ? (
        <div className={"pull-catalog-stock" + catalogStockActive}>
          <img
            src="./images/header/catalog/bed.png"
            className="pull-catalog__bed-img"
          />
        </div>
      ) : (
        <div className={"pull-catalog" + catalogActive}>
          <div className="pull-catalog-big">
            {catalogBig
              .filter((item) => item.type === catalogChapter)
              .map((catalogItem) => {
                return (
                  <div
                    className="pull-catalog-big__item"
                    style={{
                      background: `url(./images/header/catalog${catalogItem["src"]})`,
                    }}
                  >
                    <div className="pull-catalog__item__curtain">
                      <span className="pull-catalog-big__item__title">
                        {catalogItem["title"]}
                      </span>
                    </div>
                  </div>
                );
              })}
          </div>
          <div className="pull-catalog-small">
            {catalogSmall
              .filter((item) => item.type === catalogChapter)
              .map((catalogItem) => {
                return (
                  <div
                    className="pull-catalog-small__item"
                    style={{
                      background: `url(./images/header/catalog${catalogItem["src"]})`,
                    }}
                  >
                    <div className="pull-catalog__item__curtain">
                      <span className="pull-catalog-small__item__title">
                        {catalogItem["title"]}
                      </span>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </div>
  );
}

export default Catalog;
