import { useState } from "react";
import { catalog } from "./CatalogJSON";

function Catalog({
  setPull,
  pull,
  setCatalogChapter,
  catalogChapter,
  catalogMenuImgOne,
  setCatalogMenuImgOne,
  catalogMenuImgTwo,
  setCatalogMenuImgTwo,
  catalogMenuImgThree,
  setCatalogMenuImgThree,
}) {
  const [catalogMenuImgActive, setCatalogMenuImgActive] = useState("");

  const [catalogActive, setCatalogActive] = useState("");
  const [catalogActiveBg, setCatalogActiveBg] = useState("");

  return (
    <div className={"pull__main" + pull}>
      <div className="pull-menu">
        <div className="pull-menu__list">
          <span
            className="pull-menu__item"
            onClick={() => {
              setCatalogChapter("bedroom");
            }}
          >
            Спальня
          </span>
          {catalogChapter == "bedroom" ? (
            <div className="pull-catalog__list">
              {catalog
                .filter((item) => item.type === catalogChapter)
                .map((catalogItem) => {
                  return (
                    <div className="pull-catalog__item">
                      <span>{catalogItem.title}</span>
                    </div>
                  );
                })}
            </div>
          ) : (
            ""
          )}

          <span className="pull-menu__item">Одежда и обувь для дома</span>
          {catalogChapter == "clothes" ? (
            <div className="pull-catalog__list">
              {catalog
                .filter((item) => item.type === catalogChapter)
                .map((catalogItem) => {
                  return (
                    <div className="pull-catalog__item">
                      <span>{catalogItem.title}</span>
                    </div>
                  );
                })}
            </div>
          ) : (
            ""
          )}
          <span className="pull-menu__item">Кухня и интерьер</span>
          {catalogChapter == "kitchen" ? (
            <div className="pull-catalog__list">
              {catalog
                .filter((item) => item.type === catalogChapter)
                .map((catalogItem) => {
                  return (
                    <div className="pull-catalog__item">
                      <span>{catalogItem.title}</span>
                    </div>
                  );
                })}
            </div>
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
}

export default Catalog;
